import UserModel from "../Models/UserModel";

// users State - המידע ברמת האפליקציה הקשור למוצרים
export class usersState {
    public users: UserModel[] = []; // undefined חשוב לאתחל מערכים כך שלא יהיה בו
}

// users Action Type: 
export enum usersActionType {
    usersDownloaded = "usersDownloaded",
    ProductAdded = "ProductAdded",
    ProductUpdated = "ProductUpdated",
    ProductDeleted = "ProductDeleted"
}

// users Action:
export interface usersAction {
    type: usersActionType;
    payload?: any; // מטען שילוח
}

// users Action Creators: 
export function usersDownloadedAction(users: UserModel[]): usersAction {
    return { type: usersActionType.usersDownloaded, payload: users };
}
export function productAddedAction(addedProduct: UserModel): usersAction {
    return { type: usersActionType.ProductAdded, payload: addedProduct };
}
export function productUpdatedAction(updatedProduct: UserModel): usersAction {
    return { type: usersActionType.ProductUpdated, payload: updatedProduct };
}
export function productDeletedAction(id: number): usersAction {
    return { type: usersActionType.ProductDeleted, payload: id };
}

// users Reducer: 
export function usersReducer(currentState: usersState = new usersState(), action: usersAction): usersState {

    const newState = { ...currentState };

    switch (action.type) {

        case usersActionType.usersDownloaded: // payload = all users
            newState.users = action.payload;
            break;

        case usersActionType.ProductAdded: // payload = added product
            newState.users.push(action.payload);
            break;

        case usersActionType.ProductUpdated: // payload = updated product
            const indexToUpdate = newState.users.findIndex(p => p.login.username === action.payload.id);
            newState.users[indexToUpdate] = action.payload;
            break;

        case usersActionType.ProductDeleted: // payload = product id to delete
            const indexToDelete = newState.users.findIndex(p => p.login.username === action.payload);
            newState.users.splice(indexToDelete, 1);
            break;
    }

    return newState;

}
