import axios from "axios";
import { Component } from "react";
import UserModel from "../../../Models/UserModel";
import { usersDownloadedAction } from "../../../Redux/UsersState";
import store from "../../../Redux/Store";
import globals from "../../../Services/Globals";
import PleaseWait from "../../SharedArea/PleaseWait/PleaseWait";
import UsersTable from "../UsersTable/UsersTable";
// import "./";

interface UsersListState {
    users: UserModel[];
}

class UsersList extends Component<{}, UsersListState> {

    public constructor(props: {}) {
        super(props);
        this.state = { users: store.getState().usersState.users };
    }

    public async componentDidMount() {
        try {
            if (this.state.users.length === 0) {
                const response = await axios.get(globals.usersUrl); // response is a wrapper.

                this.setState({ users: response.data.results });
                store.dispatch(usersDownloadedAction(response.data.results));
            }
        }
        catch (err) {
            alert("Error");
            console.log(err);
        }
    }

    public render(): JSX.Element {
        return (
            <div className="UsersList">

                {this.state.users.length === 0 && <PleaseWait />}

                {this.state.users.map(u => <UsersTable users={u} key={u.login.username} />)}
            </div>
        );
    }
}

export default UsersList;
